mapper:







//根据学号删除

    delete from CanteenManager
    where ManagerID = #{managerID,jdbcType=INTEGER}


//插入食堂管理员信息

    insert into canteem_admin (ManagerID,ManagerName, managerPassword, canteenID)
    values (#{managerID,jdbcType=INTEGER},#{managerName,jdbcType=VARCHAR}, #{managerPassword,jdbcType=VARCHAR}, #{canteenID,jdbcType=INTEGER})


//根据工号更新信息

    update CanteenManager
    set ManagerName = #{managerName,jdbcType=VARCHAR},
        managerPassword = #{managerPassword,jdbcType=VARCHAR},
        canteenID = #{canteenID,jdbcType=INTEGER}
    where ManagerID = #{managerID,jdbcType=INTEGER}


//查询所有人信息

    select ManagerID, ManagerName, managerPassword, canteenID
    from CanteenManager
    where 1=1

        and ManagerName like concat('%',#{managerName},'%')



//登陆时查询

    select ManagerID, ManagerName, managerPassword, canteenID
    from CanteenManager
    where 1=1

        and ManagerID =#{managerID}


        and managerPassword =#{managerPassword}
